var Message = require('./message');

// Response constructor.
var Response = Message.extend({
	/**
	 * Initializer.
	 * @param config {Object}
	 */
	_initialize: function (config) {
		config = config || {};

		Response.superclass._initialize.call(this, config);

		this._status = config.status || 404;
	},

	/**
	 * Get/Set the response status code.
	 * @param [value] {number}
	 * @return {number|Object}
	 */
	status: function (value) {
		if (value) { // Setter.
			this._status = parseInt(value, 10);
			return this;
		} else { // Getter.
			return this._status;
		}
	}
});

module.exports = Response;
